% read data generated by c version of speed prediction model
% Chen-Fu Yeh, 2018/11/1
fileID = fopen('plot.txt','r');
formatSpec = 'v = %f %f %f %f %f %f %f %f %f %f %f %f %f %f %f %f %f %f %f %f %f %f %f %f %f %f %f\n';
sizeA = [27, Inf];
C_potential = fscanf(fileID,formatSpec,sizeA);
foldername = 'C_plot';
mkdir(foldername);
for i = 1:length(C_potential(:,1))
    fig = plot(C_potential(i,:));
    saveas(fig,strcat(num2str(i),'.png'));
    copyfile(strcat(num2str(i),'.png'),foldername);
end
delete *.png